package com.example.cunterscore;import android.content.res.Configuration;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.util.Log;import android.view.View;import android.widget.TextView;public class MainActivity extends AppCompatActivity {    int steam1, steam2 = 0;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);    }    public void displayScore1(int score){        TextView viewscore = (TextView) findViewById(R.id.score_team1);        viewscore.setText(String.valueOf(score));    }    public void scoreteam1(View view) {        steam1++;        displayScore1(steam1);        Log.d("MainActivity","team 1 Get score +1");    }    public void displayScore2(int score2){        TextView viewscore2 = (TextView) findViewById(R.id.score_team2);        viewscore2.setText(String.valueOf(score2));    }    public void scoreteam2(View view) {        steam2++;        displayScore2(steam2);        Log.d("MainActivity","team 2 Get score +1");    }    public void reset(View view) {        if (steam1 != 0){            steam1 = 0;            TextView viewscore = (TextView) findViewById(R.id.score_team1);            viewscore.setText(String.valueOf(steam1));        }        if (steam2 !=0){            steam2 = 0;            TextView viewscore = (TextView) findViewById(R.id.score_team2);            viewscore.setText(String.valueOf(steam2));            ;        }    }}